for path in "${paths[@]}"; do
    

    sample_id=$(basename "$path" .labeled.vcf.gz) 
    sexo=$(awk -F',' -v id="$sample_id" '$5 == id {print $11}' "$archivo_csv")

    # Contar variantes 0/1 y 1/1 en cromosomas X e Y
    het_X=$(bcftools view -r X "$path" | bcftools query -f '[%GT]\n' | grep -E '0/1'|uniq -c)
    het_Y=$(bcftools view -r Y "$path" | bcftools query -f '[%GT]\n' | grep -E '0/1'|uniq -c)
    hom_X=$(bcftools view -r X "$path" | bcftools query -f '[%GT]\n' | grep -E '1/1'|uniq -c )
    hom_Y=$(bcftools view -r Y "$path" | bcftools query -f '[%GT]\n' | grep -E '1/1'|uniq -c )

    # Escribir la fila en el archivo de salida
    echo "$sample_id,$sexo,$het_X,$het_Y,$hom_X,$hom_Y" >> "$archivo_salida"
done

